declare namespace svelteHTML {
  type UtilityNames =
    | 'p'
    | 'm'
    | 'w'
    | 'min-w'
    | 'max-w'
    | 'min-h'
    | 'max-h'
    | 'h'
    | 'z'
    | 'border'
    | 'inset'
    | 'grid'
    | 'flex'
    | 'bg'
    | 'text'
    | 'font'
    | 'op'
    | 'animate'
    | 'transition'
    | 'transform'
    | 'align'
    | 'justify'
    | 'items'
    | 'self'
    | 'block'
    | 'content'
    | 'pos'
    | 'box'
    | 'caret'
    | 'isolation'
    | 'object'
    | 'overflow'
    | 'overscroll'
    | 'resize'
    | 'underline'
    | 'list'
    | 'gradient'
    | 'divide'
    | 'gap'
    | 'ring'
    | 'icon'
    | 'container'
    | 'space'
    | 'table'
    | 'order'
    | 'place'
    | 'display'
    | 'shadow'
    | 'blend'
    | 'filter'
    | 'aspect'
    | 'backdrop'
    | 'cursor'
    | 'outline'
    | 'select'
    | 'appearance'
    | 'pointer-events';

  type VariantNames =
    | 'hover'
    | 'active'
    | 'focus'
    | 'enabled'
    | 'dark'
    | 'light'
    | 'sm'
    | 'lg'
    | 'md'
    | 'xl'
    | 'xxl'
    | 'first'
    | 'last'
    | 'children'
    | 'root'
    | 'before'
    | 'after'
    | 'all'
    | 'odd'
    | 'even';

  type AttributifyNames<Prefix extends string = ''> =
    | `${Prefix}${UtilityNames}`
    | `${Prefix}${VariantNames}`;
  // | `${Prefix}${VariantNames}:${UtilityNames}`;

  type AttributifyAttributes = Partial<Record<AttributifyNames, string>>;

  type HTMLAttributes = AttributifyAttributes;
}
